     }
   };

   return { ...baseStyles, ...typeStyles[type] };
 };

 const getIcon = (type: Toast['type']) => {
   const icons = {
     success: '‚úÖ',
     error: '‚ùå',
     warning: '‚ö†Ô∏è',
     info: 'üí¨'
