   return icons[type];
 };

 const getColor = (type: Toast['type']) => {
   const colors = {
     success: '#10b981',
     error: '#ef4444',
     warning: '#f59e0b',
     info: '#3b82f6'
   };
   return colors[type];
 };

 useEffect(() => {
   toasts.forEach(toast => {
     if (toast.duration !== 0) {
       const timer = setTimeout(() => {
         onDismiss(toast.id);
